class o{constructor(){this.animations={chain_slash:"/assets/animations/chain_slash.fbx",chain_lightning:"/assets/animations/chain_lightning.fbx"},this.vfx={chain_effect:"/assets/particles/chain_lightning.png",arc_trail:"/assets/particles/electric_arc.png"}}chainAttack(i,s){console.log(`${i} CHAIN ATTACK (${s.length} targets)`),console.log(`  Animation: ${this.animations.chain_slash}`),console.log(`  VFX: ${this.vfx.chain_effect}`);let e=100;const t=[];for(let a=0;a<s.length;a++){const n=e*Math.pow(.8,a);t.push({target:s[a],damage:Math.floor(n)}),console.log(`  Chain ${a+1}: ${s[a]} takes ${Math.floor(n)} damage`)}return t}}export{o as ChainAttackSystem};
